import { useEffect, useState } from 'react';
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';
import Form from 'react-bootstrap/Form';

import React from 'react'

function RegistrationForm(props) {
    const [modalShow, setModalShow] = useState(false);
    const [emailId, setemailId] = useState('');
    const [password, setPassword] = useState('');
    const [addressOne, setAddressOne] = useState('');
    const [addressTwo, setAddressTwo] = useState('');
    const [isButtonEnabled, setIsButtonEnabled] = useState(false);

    const handleEmailChange = (e) => {
        setemailId(e.target.value);
      };
    const handlePasswordChange = (e)=>{
        setPassword(e.target.value);
    }
    const handleAddressOneChange =(e)=>{
        setAddressOne(e.target.value);
    }
    const handleAddressTwoChange=(e)=>{
        setAddressTwo(e.target.value);
    }

    const handleButtonClick = () => {
        console.log('Button clicked');
      };

    useEffect(() => {
        if (emailId.trim() && password.trim() && addressOne.trim() && addressTwo.trim()!== '') {
          setIsButtonEnabled(true);
        } else {
          setIsButtonEnabled(false);
        }
      }, [emailId, password, addressOne, addressTwo]);


  return (
    <>
    <Button variant="primary" onClick={() => setModalShow(true)}>
        Click here to Register!
      </Button>
    <Modal show={modalShow}
        onHide={() => setModalShow(false)}
      {...props}
      size="lg"
      aria-labelledby="contained-modal-title-vcenter"
      centered
    >
    <Modal.Header closeButton>
        <Modal.Title id="contained-modal-title-vcenter">
        Registration Form
        </Modal.Title>
      </Modal.Header>
      <Modal.Body>
      <Form>
      <Form.Group className="mb-3" controlId="formBasicEmail">
        <Form.Label>Email address</Form.Label>
        <Form.Control value={emailId} onChange={handleEmailChange} type="email" placeholder="Enter email" />
      </Form.Group>
      <Form.Group className="mb-3" controlId="formBasicPassword">
        <Form.Label>Password</Form.Label>
        <Form.Control value={password} onChange={handlePasswordChange} type="password" placeholder="Password" />
      </Form.Group>
      <Form.Group className="mb-3" controlId="formBasicAddress" style={{display:'flex',justifyContent:'space-between'}}>
        <Form.Group style={{width:'48%'}}>
            <Form.Label>Address 1</Form.Label>
            <Form.Control value={addressOne} onChange={handleAddressOneChange} type="input" placeholder="Address 1" />
        </Form.Group>

        <Form.Group style={{width:'48%'}}>
            <Form.Label>Address 2</Form.Label>
            <Form.Control value={addressTwo} onChange={handleAddressTwoChange} type="input" placeholder="Address 2"  />
        </Form.Group>

      </Form.Group>


    </Form>
    </Modal.Body>
    <Modal.Footer>
      <Button variant="primary" type="submit" disabled={!isButtonEnabled} onClick={handleButtonClick}>
        Submit
      </Button>
        <Button onClick={setModalShow(true)}>Close</Button>
      </Modal.Footer>
    </Modal>
    </>
  )
}

export default RegistrationForm
