import React, { useState, useRef } from 'react'
import './Navigation.css'

function NavigationBar() {


  const [text, setText] = useState("")

  function onchange(event){
    console.log(event)
    setText(event.target.value)
  }
  const arr = [
    {
      id: 1,
      name: 'HOME',
      count: 0
    },
    {
      id: 2,
      name: 'ABOUT US',
      count: 0
    },
    {
      id: 3,
      name: 'CONTACT US',
      count: 0
    },
    {
      id: 4,
      name: 'PRICING',
      count: 0
    }
  ]

  const [textMsg, setTextMsg] = useState('Click on a button to see where you are');
  const countmsgs = useRef({}).current


    function buttonClick(name, index){
      countmsgs[name] = countmsgs[name] ? countmsgs[name] + 1 : 1
      setTextMsg('Welcome to ' + arr[index].name + ', you came here '+countmsgs[name]+' times')
    }


  return (
    <div>
      {
        arr.map((item, index) => {
          return <button key={`${item.id}_${index}`} className='navbuttoncss' onClick={()=> buttonClick(item.name, index)}>{item.name}</button>
        })
      }
      <input type="text" value={text} onChange={onchange}></input>
      <p>{textMsg}</p>

    </div>
  )
}

export default NavigationBar
