import React, { useState, useRef } from 'react'
import 'bootstrap/dist/css/bootstrap.min.css';
import { Nav } from 'react-bootstrap'

function NavigationBar() {


  const arr = [
    {
      id: 1,
      name: 'HOME',
      count: 0
    },
    {
      id: 2,
      name: 'ABOUT US',
      count: 0
    },
    {
      id: 3,
      name: 'CONTACT US',
      count: 0
    },
    {
      id: 4,
      name: 'PRICING',
      count: 0
    }
  ]

  const [textMsg, setTextMsg] = useState('Click on a button to see where you are');
  const countmsgs = useRef({}).current


    function buttonClick(name, index){
      countmsgs[name] = countmsgs[name] ? countmsgs[name] + 1 : 1
      setTextMsg('Welcome to ' + arr[index].name + ', you came here '+countmsgs[name]+' times')
    }


  return (
    <>
          <Nav className="justify-content-center">
            <Nav.Item key={`${arr[0].id}_${arr[0].id}`}  onClick={()=>buttonClick(arr[0].name, arr[0].id)}>{arr[0].name}</Nav.Item>
            <Nav.Item key={`${arr[1].id}_${arr[1].id}`}  onClick={()=>buttonClick(arr[1].name, arr[1].id)}>{arr[1].name}</Nav.Item>
            <Nav.Item key={`${arr[2].id}_${arr[2].id}`}  onClick={()=>buttonClick(arr[2].name, arr[2].id)}>{arr[2].name}</Nav.Item>
            <Nav.Item key={`${arr[3].id}_${arr[3].id}`}  onClick={()=>buttonClick(arr[3].name, arr[3].id)}>{arr[3].name}</Nav.Item>
          </Nav>

      <p>{textMsg}</p>

    </>
  )
}

export default NavigationBar

