import { useEffect, useState } from 'react';
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';
import Form from 'react-bootstrap/Form';

import React from 'react'

function FormContent(props) {
    const [inputValue, setInputValue] = useState('');
    const [isButtonEnabled, setIsButtonEnabled] = useState(true)

    const handleInputChange = (e) => {
        setInputValue(e.target.value);
      };
      useEffect(() => {
        if (inputValue.trim() !== '') {
          setIsButtonEnabled(true);
        } else {
          setIsButtonEnabled(false);
        }
      }, [inputValue]);

  return (
    <>
      <Form>
      <Form.Group className="mb-3" controlId="formBasicEmail">
        <Form.Label>Email address</Form.Label>
        <Form.Control type="email" placeholder="Enter email" onChange={handleInputChange}>{inputValue}</Form.Control>
      </Form.Group>
      <Form.Group className="mb-3" controlId="formBasicPassword">
        <Form.Label>Password</Form.Label>
        <Form.Control type="password" placeholder="Password" onChange={handleInputChange}>{inputValue}</Form.Control>
      </Form.Group>
      <Form.Group className="mb-3" controlId="formBasicAddress" style={{display:'flex',justifyContent:'space-between'}}>
        <Form.Group style={{width:'48%'}}>
            <Form.Label>Address 1</Form.Label>
            <Form.Control type="input" placeholder="Address 1" onChange={handleInputChange}>{inputValue}</Form.Control>
        </Form.Group>

        <Form.Group style={{width:'48%'}}>
            <Form.Label>Address 2</Form.Label>
            <Form.Control type="input" placeholder="Address 2" onChange={handleInputChange}>{inputValue}</Form.Control>
        </Form.Group>

      </Form.Group>   
    </Form>
    <Modal
      {...props}
      size="lg"
      aria-labelledby="contained-modal-title-vcenter"
      centered
    >
      <Modal.Header closeButton>
        <Modal.Title id="contained-modal-title-vcenter">
        Registration Form
        </Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <FormContent/>
      </Modal.Body>
      <Modal.Footer>
      <Button variant="primary" type="submit" disabled={isButtonEnabled}>
        Submit
      </Button>
        <Button onClick={props.onHide}>Close</Button>
      </Modal.Footer>
    </Modal>
    </>
  )
}

function RegistrationForm() {
  const [modalShow, setModalShow] = useState(false);


  return (
    <>
      <Button variant="primary" onClick={() => setModalShow(true)}>
        Click here to Register!
      </Button>

      <FormContent
        show={modalShow}
        onHide={() => setModalShow(false)}
      />
    </>
  );
}

export default RegistrationForm
